import React, { useRef, useReducer, Reducer } from "react";
import "./App.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import New from "pages/New";
import Diary from "pages/Diary";
import Home from "pages/Home";
import Edit from "pages/Edit";
import MyButton from "components/MyButton";
import Header from "components/Header";

// An interface for our actions
interface DataAction {
    type: string;
    payload: ProcessedComment;
    targetID?: number;
}

interface ProcessedComment {
    author: string;
    content: string;
    emotion: number;
    created_time: number;
    id: number;
}

const diaryReducer: Reducer<ProcessedComment[], DataAction> = (state, action) => {
    let newState: ProcessedComment[] = [...state];
    const { type, payload } = action;

    switch (type) {
        case "INIT":
            return payload;
        case "CREATE": {
            const newItem = {
                ...payload,
            };
            newState = [newItem, ...state];
            break;
        }
        case "DELETE":
            newState = state.filter((it) => it.id !== action.targetID);
            break;
        case "EDIT":
            newState = state.map((it) => (it.id === action.payload.id ? { ...payload } : it));
            break;
        default:
            return state;
    }
    return newState;
};

const initialData: ProcessedComment[] = [];

function App() {
    const [data, dispatch] = useReducer(diaryReducer, []);
    const dataId = useRef(0);

    return (
        <div className="App">
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/new" element={<New />} />
                    <Route path="/diary" element={<Diary />} />
                    <Route path="/edit" element={<Edit />} />
                </Routes>
            </BrowserRouter>
        </div>
    );
}

export default App;
