import Header from "components/Header";
import MyButton from "components/MyButton";
import { useNavigate } from "react-router";
import React, { useState } from "react";

const getStringDate = (date: Date) => {
    const year = date.getFullYear();
    let month = date.getMonth() + 1;
    let day = date.getDate();

    if (month < 10) {
        month = parseInt(month.toString().padStart(2, "0"));
        console.log(month);
    }
    if (day < 10) {
        day = parseInt(day.toString().padStart(2, "0"));
    }
    console.log(`${year}-${month}-${day}`);
    return `${year}-${month}-${day}`;
};

const New = () => {
    const navigate = useNavigate();
    const [date, setDate] = useState<string>(getStringDate(new Date()));

    return (
        <div>
            <Header
                headText="Write a new diary"
                leftChild={<MyButton text="Previous" onClick={() => navigate(-1)} />}
            />
            <div>
                <section>
                    <h4>What&apos;s the date?</h4>
                    <div className="input-box">
                        <input
                            className="input-date"
                            type="date"
                            value={date}
                            onChange={(e) => setDate(e.target.value)}
                        />
                    </div>
                </section>
            </div>
        </div>
    );
};

export default New;
