import React, { useState } from "react";
import { ProcessedComment } from "App";

interface Option {
    value: string;
    name: string;
}
const sortOptionList: Option[] = [
    { value: "latest", name: "latest" },
    { value: "oldest", name: "oldest" },
];

interface ControlMenuProps {
    value: Option["value"];
    onChange: (value: string) => void;
    optionList: Option[];
}

const ControlMenu: React.FC<ControlMenuProps> = ({ value, onChange, optionList }) => {
    return (
        <select value={value} onChange={(e) => onChange(e.target.value)}>
            {optionList.map((item, idx) => (
                <option key={idx} value={item.value}>
                    {item.name}
                </option>
            ))}
        </select>
    );
};
const DiaryList: React.FC<{ diaryList: ProcessedComment[] }> = (diaryList) => {
    const [sortType, setSortType] = useState("latest");

    const getProcessedDiaryList = (): ProcessedComment[] => {
        const copyList = JSON.parse(JSON.stringify(diaryList));

        const compare = (a: ProcessedComment, b: ProcessedComment) => {
            if (sortType == "latest") {
                return b.date - a.date;
            } else {
                return a.date - b.date;
            }
        };
        const sortedList = copyList.sort(compare);
        return sortedList;
    };
    return (
        <div>
            <ControlMenu value={sortType} onChange={setSortType} optionList={sortOptionList} />
            {getProcessedDiaryList.map((item) => (
                <div key={item.id}>
                    {item.content}
                    {/* <div>{item.date}</div>
                    <div>{item.content}</div> */}
                </div>
            ))}
        </div>
    );
};

DiaryList.defaultProps = {
    diaryList: [],
};

export default DiaryList;
