import Header from "./Header";
import MyButton from "./MyButton";
import { useNavigate } from "react-router";
import React, { useRef, useState } from "react";
import EmotionItem from "./EmotionItem";

const emotionList = [
    {
        emotion_id: 1,
        emotion_img: process.env.PUBLIC_URL + "/assets/emotion1.png",
        emotion_des: "Terrific",
    },
    {
        emotion_id: 2,
        emotion_img: process.env.PUBLIC_URL + "/assets/emotion2.png",
        emotion_des: "Happy",
    },
    {
        emotion_id: 3,
        emotion_img: process.env.PUBLIC_URL + "/assets/emotion3.png",
        emotion_des: "Moderate",
    },
    {
        emotion_id: 4,
        emotion_img: process.env.PUBLIC_URL + "/assets/emotion4.png",
        emotion_des: "Awful",
    },
    {
        emotion_id: 5,
        emotion_img: process.env.PUBLIC_URL + "/assets/emotion5.png",
        emotion_des: "Horrible",
    },
];

const getStringDate = (date: Date) => {
    const year = date.getFullYear();
    let month: string | number = date.getMonth() + 1;
    let day: string | number = date.getDate();

    if (month < 10) {
        month = `0${month}`;
    }

    if (day < 10) {
        day = Number(`0${day}`);
    }

    return `${year}-${month}-${day}`;
};

const DiaryEditor = () => {
    const navigate = useNavigate();
    const contentRef = useRef<HTMLTextAreaElement>(null);
    const [emotion, setEmotion] = useState<number>(3);
    const [date, setDate] = useState<string>(getStringDate(new Date()));
    const [content, setContent] = useState<string>("");

    const handleClickEmotion = (emotion: number) => {
        setEmotion(emotion);
    };

    return (
        <div className="DiaryEditor ">
            <Header
                headText="Write a new diary"
                leftChild={<MyButton text="Previous" onClick={() => navigate(-1)} />}
            />
            <div>
                <section>
                    <h4>What&apos;s the date?</h4>
                    <div className="input-box">
                        <input
                            className="input-date"
                            type="date"
                            value={date}
                            onChange={(e) => setDate(e.target.value)}
                        />
                    </div>
                </section>
                <section>
                    <h4>Today&apos;s emotion</h4>
                    <div className="input-box emotion-list-wrapper">
                        {emotionList.map((it) => (
                            <EmotionItem
                                key={it.emotion_id}
                                {...it}
                                onClick={handleClickEmotion}
                                isSelected={it.emotion_id === emotion}
                            />
                        ))}
                    </div>
                </section>
                <section>
                    <h4>Today&apos;s Diary</h4>
                    <div className="input-text-wrapper">
                        <textarea ref={contentRef} value={content} onChange={setContent} />
                    </div>
                </section>
            </div>
        </div>
    );
};

export default DiaryEditor;
