import React, { useRef, useReducer } from "react";
import "./App.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import New from "pages/New";
import Diary from "pages/Diary";
import Home from "pages/Home";
import Edit from "pages/Edit";

interface DataAction {
    type: string;
    payload: ProcessedComment;
}
interface ProcessedComment {
    content: string;
    emotion: number;
    date: number;
    id: number;
}

const diaryReducer = (state: ProcessedComment[], action: DataAction): ProcessedComment[] => {
    let newState: ProcessedComment[] = [];
    const { type, payload } = action;

    switch (type) {
        case "INIT":
            return [payload];
        case "CREATE": {
            const newItem = {
                ...payload,
            };
            newState = [newItem, ...state];
            break;
        }
        case "DELETE":
            newState = state.filter((it) => it.id !== action.targetID);
            break;
        case "EDIT":
            newState = state.map((it) => (it.id === action.payload.id ? { ...payload } : it));
            break;
        default:
            return state;
    }
    return newState;
};

const initialData: ProcessedComment[] = [];

function App() {
    const [data, dispatch] = useReducer(diaryReducer, []);
    const dataId = useRef(0);

    const onCreate = (date: number, content: string, emotion: number) => {
        dispatch({
            type: "CREATE",
            payload: {
                id: dataId.current,
                date: new Date(date).getTime(),
                content,
                emotion,
            },
        });
        dataId.current += 1;
    };

    const onEdit = (targetId: number, date: number, content: string, emotion: number) => {
        dispatch({
            type: "EDIT",
            payload: {
                id: targetId,
                date: new Date(date).getTime(),
                content,
                emotion,
            },
        });
    };
    return (
        <div className="App">
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/new" element={<New />} />
                    <Route path="/diary" element={<Diary />} />
                    <Route path="/edit" element={<Edit />} />
                </Routes>
            </BrowserRouter>
        </div>
    );
}

export default App;
