import React, { useRef, useReducer } from "react";
import "./App.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import New from "pages/New";
import Diary from "pages/Diary";
import Home from "pages/Home";
import Edit from "pages/Edit";
import MyButton from "components/MyButton";
import Header from "components/Header";

// An interface for our actions
interface DataAction {
    type: string;
    payload: ProcessedComment;
    targetID?: number;
}

interface ProcessedComment {
    author: string;
    content: string;
    emotion: number;
    created_time: number;
    id: number;
}

const diaryReducer = (state: ProcessedComment[], action: DataAction) => {
    let newState: Partial<ProcessedComment[]> = [];
    const { type, payload } = action;

    switch (type) {
        case "INIT":
            return payload;
        case "CREATE": {
            const newItem = {
                ...payload,
            };
            newState = [newItem, ...state];
            break;
        }
        case "DELETE":
            newState = state.filter((it) => it.id !== action.targetID);
            break;
        case "EDIT":
            newState = state.map((it) => (it.id === action.payload.id ? { ...payload } : it));
            break;
        default:
            return state;
    }
};

function App() {
    const [state, dispatch] = useReducer(diaryReducer, []);
    const userID = useRef(0);

    const onCreate = (date: number, content: string, emotion: number) => {
        dispatch({
            type: "CREATE",
            payload: {
                id: userID.current,
                date: new Date(date).getTime(),
                content,
                emotion,
            },
        });
        userID.current += 1;
    };

    return (
        <div className="App">
            <Header
                headText={"App"}
                leftChild={<MyButton text={"left button"} onClick={() => alert("left clicked")} />}
                rightChild={<MyButton text={"right button"} onClick={() => alert("right clicked")} />}
            ></Header>
            <BrowserRouter>
                <img src={process.env.PUBLIC_URL + "/assets/emotion1.png"} />
                <img src={process.env.PUBLIC_URL + "/assets/emotion2.png"} />
                <img src={process.env.PUBLIC_URL + "/assets/emotion3.png"} />
                <img src={process.env.PUBLIC_URL + "/assets/emotion4.png"} />
                <img src={process.env.PUBLIC_URL + "/assets/emotion5.png"} />
                <MyButton text={"Button"} type="positive" onClick={() => alert("button pressed")} />
                <MyButton text={"Button"} type="negative" onClick={() => alert("button pressed")} />
                <MyButton text={"Button"} onClick={() => alert("button pressed")} />

                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/new" element={<New />} />
                    <Route path="/diary" element={<Diary />} />
                    <Route path="/edit" element={<Edit />} />
                </Routes>
            </BrowserRouter>
        </div>
    );
}

export default App;
